mapper.select:
SELECT id,course_name AS courseName,course_type_id AS courseTypeID,user_id AS userID, 
	time,click_volume AS clickVolume,picture_path AS picturePath,course_path AS coursePath,quantity
FROM course WHERE id=#{id}

SELECT id,course_name AS courseName,course_type_id AS courseTypeID,user_id AS userID, 
	time,click_volume AS clickVolume,picture_path AS picturePath,course_path AS coursePath,quantity
FROM course WHERE user_id=#{userID}

SELECT id,course_name AS courseName,course_type_id AS courseTypeID,user_id AS userID, 
	time,click_volume AS clickVolume,picture_path AS picturePath,course_path AS coursePath,quantity
FROM course	WHERE course_type_id=#{courseTypeID} ORDER BY ${link} ${sort} LIMIT 0,${num}

SELECT id,course_name AS courseName,course_type_id AS courseTypeID,user_id AS userID, 
	time,click_volume AS clickVolume,picture_path AS picturePath,course_path AS coursePath,quantity
FROM course ORDER BY ${link} ${sort} LIMIT ${begin},${end}

mapper.update:
UPDATE course SET id=#{id}
,course_name=#{courseName}
,course_type_id=#{courseTypeID}
,user_id=#{userID}
,time=#{time,jdbcType=TIMESTAMP}
,click_volume=#{clickVolume}
,picture_path=#{picturePath}
,course_path=#{coursePath}
,quantity=#{quantity}
WHERE id=#{id}
